import React from "react";
import Buttons from "./Buttons";

type Props = {
  task: {
    id: string;
    title: string;
    content: string;
  };
  setEditedTask: Function;
  deleteTask: Function;
};

const Tasks: React.FC<Props> = (props) => {
  return (
    <div>
      <li key={props.task.id}>
        {props.task.title}
        {props.task.content}
        <Buttons
          editedTask={props.task}
          text="編集"
          crudTask={() => props.setEditedTask(props.task)}
        />
        <button onClick={() => props.setEditedTask(props.task)}>編集</button>
        <button onClick={() => props.deleteTask(props.task.id)}>削除</button>
      </li>
    </div>
  );
};

export default Tasks;
