import React from "react";
import Buttons from "./IconButtons";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: "100%",
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
    },
  })
);

type Props = {
  task: {
    id: string;
    title: string;
    content: string;
  };
  setEditedTask: Function;
  deleteTask: Function;
};

const Tasks: React.FC<Props> = (props) => {
  const classes = useStyles();
  return (
    <div>
      <List component="nav" className={classes.root} key={props.task.id}>
        <ListItem button>
          {props.task.title}
          <br />
          {props.task.content}
          <Buttons
            editedTask={props.task}
            isType="edit"
            crudTask={() => props.setEditedTask(props.task)}
          />
          <Buttons
            editedTask={props.task}
            isType="delete"
            crudTask={() => props.deleteTask(props.task.id)}
          />
        </ListItem>
        <Divider />
      </List>
    </div>
  );
};

export default Tasks;
